import React, {useEffect, useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Typography from '@material-ui/core/Typography';
import {getUserByIdDB} from "@/utils/api/services";

const defaultImageBase64 = "data:image/jpeg;base64,";

const useStyles = makeStyles((theme) => ({
    imageContainer: {
        width: '100%',
        height: 400, // Adjust the height as per your requirement
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
    },
    image: {
        width: 'auto',
        height: '100%',
        objectFit: 'contain',
    },
}));

const options = {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: 'numeric',
    minute: 'numeric',
    second: 'numeric',
    timeZoneName: 'short'
}

const ItemPopup = ({ item, open, onClose, isContactDetailsSecret }) => {
    const classes = useStyles();
    const [owner, setOwner] = useState();

    const fetchOwner = async () => {
        if (item) {
            try {
                const response = await getUserByIdDB(item.user_id);
                setOwner(response);
            } catch (error) {
                console.error('Error fetching initial data:', error);
            }
        }
    };

    useEffect(() => {
        fetchOwner();
    }, []);

    const handleClose = () => {
        onClose();
    };

    return (
        <Dialog open={open} onClose={handleClose} aria-labelledby="item-dialog-title">
            <DialogTitle id="item-dialog-title">{item.title}</DialogTitle>
            <DialogContent>
                <div className={classes.imageContainer}>
                    <img className={classes.image} src={item.image || defaultImageBase64} alt={item.title} />
                </div>
                <DialogContentText> <br/>
                    <Typography variant="h6" style={{display: "block", color: "#824D74"}}>Owner: {owner && owner.user_metadata.custom_name} {owner && owner.user_metadata.custom_surname}</Typography><br/>
                    {(item.shareContact || !isContactDetailsSecret) &&
                        <div style={{marginBottom:"3%"}}>
                            <Typography variant="h6" style={{display: "block", fontWeight:"bold"}} gutterBottom> Owner's contact details </Typography>
                            <Typography variant="h8" style={{display: "block"}} gutterBottom>Email: {owner && owner.email}</Typography>
                            <Typography variant="h8" style={{display: "block"}} gutterBottom>Phone: {owner && owner.user_metadata.custom_phone}</Typography>
                            <Typography variant="h8" style={{display: "block"}} gutterBottom>Address: {owner && owner.user_metadata.custom_address}</Typography>
                        </div>
                    }
                    <Typography variant="h6" style={{display: "block", color: "#25aae1"}}>Price: {item.price} {item.currency}</Typography><br/>
                    <Typography variant="h6"
                                style={{display: "block", color:"#FE7A83"}}>Description: {item.description}</Typography><br/>
                    <Typography variant="h7" style={{display: "block"}}>Creation
                        Date: {new Date(item.creation_date).toLocaleString(options).replace(" ", ",")}</Typography>
                    <Typography variant="h7" style={{display: "block"}}>Last
                        update: {new Date(item.update_date).toLocaleString(options).replace(" ", ",")}</Typography><br/>
                    {item.flatFields.map((item) => (<Typography variant="h7"
                                                                style={{display: "block"}}>{item.fieldName}: {item.fieldValue}</Typography>))}<br/>
                    {item.nestedFields.map((nestedField) => (
                        <div>
                            <Typography variant="h6" style={{display: "block", fontWeight:"bold"}}>{nestedField.name}</Typography>
                            {nestedField.fields.map((subfield) => (
                                <Typography variant="h7" style={{display: "block"}}>{subfield.fieldName}: {subfield.fieldValue}</Typography>
                            ))}
                            <br/>
                        </div>
                    ))}

                    {item.listFields.map((listField) => (
                        <div>
                            <Typography variant="h6" style={{display: "block", fontWeight:"bold"}}>{listField.name}</Typography>
                            <ul>
                                {listField.list.map((item) => (
                                    <li>{item.value}</li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </DialogContentText>
            </DialogContent>

            <DialogActions>
                <Button onClick={handleClose} color="primary">
                    Close
                </Button>
            </DialogActions>
        </Dialog>
    );
};

export default ItemPopup;
